mapper.insert:
INSERT INTO
	MEMBER (USER_ID
		  , USER_PWD
		  , USER_NAME
		  , EMAIL
		  , GENDER
		  , AGE
		  , PHONE
		  , ADDRESS)
	VALUES (#{userId}
		  , #{userPwd}
		  , #{userName}
		  , #{email}
		  , #{gender}
		  , #{age}
		  , #{phone}
		  , #{address})

mapper.update:
		UPDATE MEMBER
   		   SET NAME = #{userName}
   		   	 , EMAIL = #{email}
   		   	 , AGE = #{age}
   		   	 , PHONE = #{phone}
   		   	 , ADDRESS = #{address}
   		     , GENDER = #{gender}
   		 WHERE USER_ID = #{userId}
   		 	   AND STATUS = 'Y'
